function base64UrlEncode(obj) {
  var buf = Buffer.from(JSON.stringify(obj), 'utf8');
  var base64Str = buf.toString('base64');
  console.log(base64Str);
  return base64Str.replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

function fromBase64(base64) {
  return base64
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

let header = {
  'alg': 'RS256',
  'typ': 'JWT'
};
let payload = {
  'sub': '1234567890',
  'name': 'John Doe',
  'admin': true
};


var securedInput = `${base64UrlEncode(header)}.${base64UrlEncode(payload)}`;
console.log(securedInput);

var signer = crypto.createSign('RSA-SHA256');
var signature = (signer.update(securedInput), signer.sign(privateKey, 'base64'));

signature = fromBase64(signature);

var encrypted = `${securedInput}.${signature}`;
